Select p.id as Project_id, p.customer_display_name, p.customer_email, p.customer_phone,
bu.name as PM_name, bu.email as PM_email, bu1.name as CM_name, bu1.email as CM_email,
bu2.name as GM_name, bu2.email as GM_email, bu3.name as id_dp_name, bu3.email as id_dp_email

from launchpad_backend.projects as p 

left join 
(
select ps.Project_id, bu.name, bu.email
from launchpad_backend.project_settings as ps 
left join launchpad_backend.bouncer_users as bu on bu.id = ps.primary_pm_id
where ps.is_deleted = 0 
) as bu on bu.project_id = p.id

left join 
(
select ps.Project_id, bu.name, bu.email
from launchpad_backend.project_settings as ps 
left join launchpad_backend.bouncer_users as bu on bu.id = ps.primary_cm_id
where ps.is_deleted = 0 
) as bu1 on bu1.project_id = p.id

left join 
(
select ps.Project_id, bu.name, bu.email
from launchpad_backend.project_settings as ps 
left join launchpad_backend.bouncer_users as bu on bu.id = ps.primary_gm_id
where ps.is_deleted = 0 
) as bu2 on bu2.project_id = p.id

left join 
(
select ps.Project_id, bu.name, bu.email
from launchpad_backend.project_settings as ps 
left join launchpad_backend.bouncer_users as bu on bu.id = ps.primary_designer_id
where ps.is_deleted = 0 
) as bu3 on bu3.project_id = p.id

